package AllPrograms;

public class Even_Odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=89;
		System.out.println("Even or odd no");
		if(a%2==0)
		{
			System.out.println("even no is: "+a);
		}
		else
		{
			System.out.println("odd no is: "+a);
		}
	}

}
