package Loop;

public class Nested_loopH {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int b=1;b<=7;b++)
		{
			for(int a=1;a<=b;a++)
			{
				System.out.print("*");
			}
			
			System.out.println();
		}
		
		for(int i=6;i>=1;i--)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("*");
			}
			//System.out.print(" ");
			System.out.println();
			
		}

	}

}
