package Loop;

public class Nestsed_loopD {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int b=1;b<=7;b++)
		{
			for(int a=1;a<=b;a++)
			{
				System.out.print("*");
			}
			System.out.println();
		}
	}

}

