package Loop;

public class Simple_forA {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Use of simple for loop ");
		
		for(int a=1;a<8;a++)//7
		{
			for(int b=1;b<3;b++)//2
			{
				
				System.out.print("0");
			}
			
			System.out.println();
		}
	}				

}		




