package Loop;

public class nested_forD {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("*  Implementation of Nestse For loop  *");

		for(int b=1;b<=4;b=b+2)
		{
			for(int a=1;a<=5;a++)
			{
				
				System.out.print("*");
			}
			
			//b++;
			System.out.println();
		}
	}

}
