package all_12_04_2021;

public class simple_Program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Print HI using break");
		
		for(int i=0;i<5;i--)
		{
			System.out.println("HI");
			
			if(i==-2)
			{
				break;
			}
		}
	}

}
