package AllPrograms;

public class Evenodd_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("To check given no is  even");
		for(int i=0;i<20;i++)
		{
			if(i%2==0)
			{
				System.out.println(i);
			}
		}
		System.out.println("To check given no is  odd");
		for(int i=0;i<20;i++)
		{
			if(i%2!=0)
			{
				System.out.println(i);
			}
		}
	}

}
