package All_23_04_2021;

public class Large3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=43;
		int b=64;
		int c=48;
		if(a>b)
		{
			if(a>c)
			{
				System.out.println(a+ " is large");
			}
			else
			{
				System.out.println(c+ " is large");
			}
		}
		else
		{
			if(b>c)
			{
				System.out.println(b+ " is large");
			}
			else
			{
				System.out.println(c+ " is large");
			
			}
		}
	}

}
