package FactorialNo;

public class For_FactorialNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int no=4,fact=1;
		System.out.println("WAP to print factorial no");
		for(int i=1;i<=no;i++)
		{
			fact=fact*i;
		}
		System.out.println(fact);
	}

}
