package Loop;

public class Nested_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("*  Implementation of Nestse For loop  *");

		for(int b=1;b<4;b++)
		{
			for(int a=1;a<6;a++)
			{
				//System.out.println("Print line for 15 times: ");
				System.out.print("*");
			}
			
			System.out.println("Print line for 15 times: ");
		}
	}

}
