package Loop;

public class Nested_forB {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("*  Implementation of Nestse For loop  *");

		for(int b=1;b<=4;b++)
		{
			for(int a=1;a<=5;a++)
			{
				
				System.out.print("*");
			}
			
			System.out.println();
		}
	}

}
