package Loop;

public class Nested_forE {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("*  Implementation of Nestse For loop  *");

		for(int  b=7;b>=1;b--)
		//for(int b=1;b<8;b++)
		{
			//for(int a=8;a>b;a--)
			for(int a=1;a<=b;a++)
			{
				
				System.out.print("*");
				
			}
			
			
			System.out.println();
		}

	}

}

