package Loop;

public class for_loopB {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		System.out.println("Use of simple for loop ");
		
		for(int a=8;a>1;a--)//7		
		{
			for(int b=1;b<a;b++)
				
			{
				
				System.out.print("*");
			}
			
			System.out.println(0);
		}
	}				

}		

