
package variable_operation;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a;
		int b;
		int c;
		char S;
		float Variable_value;
		String x;
		
		a=10;
		b=20;
		S='V';
		Variable_value=10.10f;
		x="Variable  operations should get perform";
		
		c=a+b;
		
		System.out.println("Value of c is: "+c);
		System.out.println(" Value of a is: "+a);
		System.out.println("Character value is getting printed: "+S);
		System.out.println("Value of float is: "+Variable_value);
		System.out.println(+a+" : "+b);
		System.out.println("Value of String: "+x);
		System.out.println(a);
		System.out.println(c);
		System.out.println('a');
		  
		a=200;
		
		System.out.println("Value of c is: "+c);
		System.out.println(a);
		
		a=a+100;
		
		System.out.println(a);
		System.out.println("Value of a is: "+a);
		
		b=1000;
		
		System.out.println("Value of b is: "+b);
		
		b=a*b;
		
		System.out.println("Value of b=a*b is: "+b);
		System.out.println('b');
		
	}

}
